// packages/audio/index.js
// Sample-based sound effects embedded as base64 strings (no binary assets).
export function attachSfx(engine, context = null) {
  const AudioCtx = window.AudioContext || window.webkitAudioContext;
  const ctx = context || new AudioCtx();

  const buffers = { shot: {}, hit: {}, kill: null };
  const B64 = {
  "shot-fire": "",
  "shot-ice": "",
  "shot-light": "",
  "shot-poison": "",
  "hit-fire": "",
  "hit-ice": "",
  "hit-light": "",
  "hit-poison": "",
  "kill": ""
};

  function b64ToBuf(b64) {
    const bin = atob(b64);
    const len = bin.length;
    const buf = new Uint8Array(len);
    for (let i = 0; i < len; i++) buf[i] = bin.charCodeAt(i);
    return buf.buffer;
  }

  async function load(name) {
    const buf = b64ToBuf(B64[name]);
    return ctx.decodeAudioData(buf);
  }

  (async () => {
    buffers.shot.FIRE = await load('shot-fire');
    buffers.shot.ICE = await load('shot-ice');
    buffers.shot.LIGHT = await load('shot-light');
    buffers.shot.POISON = await load('shot-poison');

    buffers.hit.FIRE = await load('hit-fire');
    buffers.hit.ICE = await load('hit-ice');
    buffers.hit.LIGHT = await load('hit-light');
    buffers.hit.POISON = await load('hit-poison');

    buffers.kill = await load('kill');
  })();

  function play(buf) {
    if (!buf) return;
    const src = ctx.createBufferSource();
    src.buffer = buf;
    src.connect(ctx.destination);
    src.start();
  }

  window.addEventListener('pointerdown', () => {
    if (ctx.state !== 'running') ctx.resume();
  }, { once: true });

  engine.hook('shot', ({ towerId }) => {
    const t = engine.state.towers.find(tt => tt.id === towerId);
    play(t && buffers.shot[t.elt]);
  });

  engine.hook('creepDamage', ({ elt }) => {
    play(buffers.hit[elt]);
  });

  engine.hook('creepKill', () => {
    play(buffers.kill);
  });

  return { play, ctx, buffers };
}
